import React, { useState } from 'react';
import { View, StyleSheet, Text, Modal, TextInput, Button } from 'react-native';

const ChangeStatModal = (props) => {
    const [statInput, setStatInput] = useState('');

    const statInputHandler = (enteredText) => {
        setStatInput(enteredText);
    };

    const checker = () => {
        console.log(statInput);
    };

    return(
        <Modal animationType='slide' visible={props.visible}>
            <View style={styles.container}>
                <TextInput sytle={styles.input} placeholder='Stat Value' keyboardType='numeric' onChangeText={statInputHandler}/>
                <View>
                    <Button title="Change" onPress={() => {props.statChange(true)}}/>
                    <Button title="Cancel"/>
                </View>
            </View>
        </Modal>
    );
}

const styles = StyleSheet.create({
    container:{
        padding: 50,
    },
    input: {
        padding:10,
        margin:10,
        width:20,
        borderColor:"black",
        borderWidth: 1,
        color: 'white',
    }
})

export default ChangeStatModal;
