import React, {useState} from 'react';
import { View, StyleSheet, Text } from 'react-native';

import StatBox from '../components/StatBox';
import ChangeStatModal from '../components/ChangeStatModal';

function CharacterWindow(props) {
    const [isChangeStat, setIsChangeStat] = useState(false);

    const statChangeView = (visible) => {
        if(isChangeStat){
            setIsChangeStat(false);
        }else{
            setIsChangeStat(true);
        }
        // setIsChangeStat(visible);
    }

    return (
        <View style={[styles.container]}>
            <StatBox name='STR' num='13'/>
            <StatBox name='DEX' num='13' statChange={statChangeView} />
            <StatBox name='CON' num='15'/>
            <StatBox name='INT' num = '12'/>
            <StatBox name='WIS' num='9'/>
            <StatBox name='CHA' num='7'/>
            <ChangeStatModal visible={isChangeStat}/>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        paddingTop: 60,
        flex: 1,
        flexWrap: 'wrap',
        flexDirection: 'row',
        backgroundColor: '#404040',
        alignItems: 'center',
        justifyContent: 'space-evenly',
        width:'100%',
        height:'100%',
    },
})

export default CharacterWindow;
