import React, { useEffect, useState } from "react";
import { firestore, auth } from "../firebase";
import { View, Text, StyleSheet, TouchableOpacity, ScrollView } from "react-native";
import { useFocusEffect } from "@react-navigation/core";
import textStyles from "../styles/TextStyles";
import CharacterCard from "../components/CharacterCard";

export default function CharacterSelectScreen(props) {
	const [characters, setCharacters] = useState([]);

	useFocusEffect(
		React.useCallback(() => {
			console.log("hit");
			firestore
				.collection("users")
				.doc(auth.currentUser.uid)
				.collection("characters")
				.get()
				.then((snapshot) => {
					const thing = snapshot.docs;
					setCharacters(thing);
				})
				.catch((error) => {
					console.log(error);
				});
		}, [])
	);

	// useEffect(() => {
	// 	console.log("hit");
	// 	firestore
	// 		.collection("users")
	// 		.doc(auth.currentUser.uid)
	// 		.collection("characters")
	// 		.get()
	// 		.then((snapshot) => {
	// 			const thing = snapshot.docs;
	// 			setCharacters(thing);
	// 		})
	// 		.catch((error) => {
	// 			console.log(error);
	// 		});
	// }, []);

	const selectCharacter = (index) => {
		props.navigation.navigate("CharacterPage", {
			character: characters[index].data(),
		});
	};

	const handleNewCharacter = () => {
		props.navigation.navigate("CharacterCreation");
	};

	return (
		<View style={styles.main}>
			<ScrollView style={{ width: "100%", backgroundColor: "#404040" }} contentContainerStyle={styles.container}>
				{characters.map((character, index) => {
					return (
						<CharacterCard
							key={character.id}
							index={index}
							name={character.data().name}
							select={selectCharacter}
						/>
					);
				})}
			</ScrollView>
			<View style={styles.buttonContainer}>
				<TouchableOpacity
					style={styles.button}
					onPress={() => {
						handleNewCharacter();
					}}>
					<Text style={textStyles.mainText}> New Character</Text>
				</TouchableOpacity>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	main: {
		flex: 1,
		backgroundColor: "#404040",
		width: "100%",
		height: "100%",
		alignItems: "center",
	},
	container: {
		paddingTop: 20,
		flex: 1,
		flexDirection: "column",
		backgroundColor: "#404040",
		alignItems: "center",
		width: "100%",
	},
	buttonContainer: {
		width: "100%",
		height: 120,
		alignItems: "center",
	},
	button: {
		justifyContent: "center",
		alignItems: "center",
		width: "90%",
		height: 60,
		backgroundColor: "#29293d",
		margin: 20,
	},
});
