import React, { useState } from "react";
import { Button, TextInput, View, StyleSheet, Alert } from "react-native";
import { auth, firebase } from "../firebase";
import { useDispatch } from "react-redux";
import { setActiveUser } from "../redux/features/userSlice";

function Register(props) {
	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const dispatch = useDispatch();

	const onSignUp = () => {
		auth.createUserWithEmailAndPassword(email, password)
			.then((result) => {
				firebase.firestore().collection("users").doc(auth().currentUser.uid).set({ name, email });
				dispatch(setActiveUser(result));
				// dispatch(updateUser());
			})
			.catch((error) => {
				Alert.alert(error);
			});
	};

	return (
		<View style={styles.container}>
			<TextInput style={styles.input} placeholder='name' onChangeText={(name) => setName(name)} />
			<TextInput
				style={styles.input}
				keyboardType='email-address'
				placeholder='email'
				onChangeText={(email) => setEmail(email)}
			/>
			<TextInput
				style={styles.input}
				placeholder='password'
				secureTextEntry={true}
				onChangeText={(pw) => setPassword(pw)}
			/>
			<Button title='Register' onPress={() => onSignUp()} />
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		paddingTop: 50,
		backgroundColor: "#fff",
		alignItems: "center",
	},
	input: {
		width: "80%",
		padding: 10,
		margin: 10,
		borderWidth: 1,
		borderColor: "black",
		color: "black",
	},
});

export default Register;
