import React, { useState } from "react";
import { View, Text, StyleSheet } from "react-native";
import { useSelector } from "react-redux";
import { selectCharacter } from "../redux/features/characterSlice";

export default function HealthScreen() {
	const [character, setCharacter] = useState(useSelector(selectCharacter));

	return (
		<View style={styles.main}>
			<View style={styles.header}>
				<View style={styles.headerTitleContainer}>
					<Text style={styles.headerTitle}>HP Management</Text>
				</View>
			</View>
			<View style={styles.hpContainer}>
				<View style={[styles.hpBox]}>
					<Text style={styles.hpNumber}>{character.health}</Text>
					<Text style={styles.hpText}>Current HP</Text>
				</View>
				<View style={styles.dashBox}>
					<Text style={{ color: "#aaa", fontSize: 35 }}>/</Text>
				</View>
				<View
					style={[
						styles.hpBox,
						{ backgroundColor: "#404040", borderRightColor: "#404040", borderRightWidth: 2 },
					]}>
					<Text style={styles.hpNumber}>{character.maxHealth}</Text>
					<Text style={styles.hpText}>Maximum HP</Text>
				</View>
				<View style={styles.modContainer}></View>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	main: {
		flex: 1,
		backgroundColor: "#404040",
		alignItems: "center",
	},
	header: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		backgroundColor: "#303030",
		height: 55,
		width: "100%",
		marginBottom: 30,
	},
	headerTitleContainer: {
		justifyContent: "center",
		alignItems: "center",
	},
	headerTitle: {
		color: "white",
		fontSize: 18,
		fontWeight: "500",
	},
	hpContainer: {
		flexDirection: "row",
		flexWrap: "wrap",
		width: "95%",
		justifyContent: "space-evenly",
		alignItems: "center",
		backgroundColor: "#303030",
	},
	hpBox: {
		flex: 1,
		justifyContent: "center",
		alignItems: "center",
		height: 100,
		width: 150,
	},
	dashBox: {
		justifyContent: "center",
		alignItems: "center",
		height: 100,
		width: 70,
		backgroundColor: "#404040",
	},
	hpNumber: {
		color: "white",
		fontSize: 35,
	},
	hpText: {
		color: "#aaa",
	},
	modContainer: {
		width: "100%",
		height: 100,
	},
});
